package com.rkoch.book.library.entities;

import java.util.ArrayList;
import java.util.List;
import com.rkoch.book.library.entities.definition.Entity;
import java.util.Objects;
/**
 *
 * @author rkoch
 */
public class Customer 
        implements Entity<Long>{
    
    //Autogenerated PK
    private long id;
    private String fname;
    private String lname;
    private String email;
    
    //One to Many Mapping
    private final List<BookOrder> orders = new ArrayList();

    public Customer(String email) {
        this.email = email;
    }
    
    @Override
    public Long getId() {
        return this.id;
    }

    @Override
    public void setId(Long id) {
        this.id = id;
    }

    public String getFname() {
        return fname;
    }

    public void setFname(String fname) {
        this.fname = fname;
    }

    public String getLname() {
        return lname;
    }

    public void setLname(String lname) {
        this.lname = lname;
    }

    public String getEmail() {
        return email;
    }

    public void setEmail(String email) {
        this.email = email;
    }
    
    public List<BookOrder> getHistory(){
        return this.orders;
    }

    @Override
    public int hashCode() {
        int hash = 7;
        hash = 97 * hash + Objects.hashCode(this.email);
        return hash;
    }

    @Override
    public boolean equals(Object obj) {
        if (this == obj) {
            return true;
        }
        if (obj == null) {
            return false;
        }
        if (getClass() != obj.getClass()) {
            return false;
        }
        final Customer other = (Customer) obj;
        return Objects.equals(this.email, other.email);
    }

    @Override
    public String toString() {
        return "Customer{" + "id=" + id + ", fname=" + fname + ", lname=" + lname + ", email=" + email + '}';
    }  
}
